INSERT INTO movie(movie_collection_id,original_language_id,imdb_id,original_title,overview,homepage,backdrop_path,poster_path,release_date,release_status_code,budget,revenue,runtime,popularity,adult,video) VALUES (NULL,(SELECT language_id FROM language WHERE iso_639_1 = 'en'),'tt0396101','Little Frank and His Carp','A single channel video featuring the American artist Andrea Fraser as she walks around the atrium of the Guggenheim Bilbao listening to the institution’s audio guide. After an initial shot of the entrance, the video shows Fraser wearing a short green dress and high heels picking up an audio guide inside. Once she begins listening to the guide the soundtrack changes so that the viewer also hears the same male voice as the artist (and without any background noise). Shot with five hidden cameras – one on Fraser herself and four others operated by assistants moving around the atrium – Little Frank and His Carp depicts Fraser from varying angles and proximities as she initially follows the audio guide’s instructions closely, her emotions visibly changing in response to the material she hears. Told that modern art is ‘demanding, complicated, bewildering’, she appears anxious, but when the guide tells her ‘the museum tries to make you feel at home’, she immediately seems reassured and happy.',NULL,NULL,NULL,'2001-01-01',0,0,0,6,0.6,false,false);
